(1, 'order_receipt', 'order', 'edd', 'customer', 'Purchase Receipt', 'Purchase Receipt', 'Dear {name},\n\nThank you for your purchase. Please click on the link(s) below to download your files.\n\n{download_list}\n\n{sitename}', 1, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(2, 'admin_order_notice', 'order', 'edd', 'admin', 'New download purchase - Order #{payment_id}', 'New Sale!', 'Hello\n\nA Downloads purchase has been made.\n\nDownloads sold:\n\n{download_list}\n\nPurchased by: {fullname}\nAmount: {price}\nPayment Method: {payment_method}\n\nThank you', 1, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(3, 'order_refund', 'refund', 'edd', 'customer', 'Your order has been refunded', '', 'Dear {name},\n\nYour order has been refunded.', 0, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(4, 'admin_order_refund', 'refund', 'edd', 'admin', 'An order has been refunded', '', 'Order {payment_id} has been refunded.', 0, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(5, 'new_user', 'user', 'edd', 'customer', '[{sitename}] Your username and password', 'Your account info', 'Username: {username}\r\nPassword: [entered on site]\r\n<a href=\"http://localhost/todo-app/wp-login.php\"> Click here to log in &rarr;</a>\r\n', 1, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(6, 'new_user_admin', 'user', 'edd', 'admin', '[{sitename}] New User Registration', 'New user registration', 'Username: {username}\r\n\r\nE-mail: {user_email}\r\n', 1, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(7, 'user_verification', 'user', 'edd', 'user', 'Verify your account', 'Verify your account', 'Hello {fullname},\n\nYour account with {sitename} needs to be verified before you can access your order history.\n\nVisit this link to verify your account: {verification_url}\n\n', 1, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(8, 'password_reset', 'user', 'wp', 'user', '[Todo] Password Reset', '', 'Someone has requested a password reset for the following account:\r\n\r\nSite Name: {sitename}\r\n\r\nUsername: {username}\r\n\r\nIf this was a mistake, ignore this email and nothing will happen.\r\n\r\nTo reset your password, visit the following address:\r\n\r\n{password_reset_link}\r\n\r\nThis password reset request originated from the IP address {ip_address}.\r\n', 0, '2024-08-13 21:38:04', '2024-08-13 21:38:04'),
(9, 'stripe_early_fraud_warning', 'order', 'edd', 'admin', 'Stripe Early Fraud Warning - Order #{payment_id}', 'Possible Fraudulent Order', 'Hello\n\nStripe has detected a potential fraudulent order.\n\nDownloads sold:\n\n{download_list}\n\nPurchased by: {fullname}\nAmount: {price}\n<a href=\"{order_details_link}\">Order Details</a>\n\nNote: Once you have reviewed the order, ensure you take the appropriate action within your Stripe dashboard to help improve future fraud detection.', 0, '2024-08-13 21:38:04', '2024-08-13 21:38:04');
